package com.roadster.controllers;

import javafx.fxml.FXML;
import javafx.scene.layout.BorderPane;
import javafx.scene.control.ToolBar;
import com.roadster.views.*;

/**
 * Main Controller - Handles navigation between different views
 * This is the central controller for the application
 */
public class MainController {

    @FXML
    private BorderPane mainContainer;


    // View instances
    private LoginView loginView;
    private DashboardView dashboardView;
    private DriversView driversView;
    private MapsView mapsView;
    private PoliceBoxView policeBoxView;
    private UserProfileView userProfileView;

    @FXML
    public void initialize() {
        // Initialize all views
        loginView = new LoginView(this);
        dashboardView = new DashboardView(this);
        driversView = new DriversView(this);
        mapsView = new MapsView(this);
        policeBoxView = new PoliceBoxView(this);
        userProfileView = new UserProfileView();

        // Set default view (login)
        showLoginView();

    }


    @FXML
    private void showLoginView() {
        mainContainer.setCenter(loginView);
    }

    @FXML
    public void showDashboardView() {
        mainContainer.setCenter(dashboardView);
    }

    @FXML
    public void showDriversView() {
        mainContainer.setCenter(driversView);
    }

    @FXML
    public void showMapsView() {
        mainContainer.setCenter(mapsView);
    }

    @FXML
    public void showPoliceBoxView() {
        mainContainer.setCenter(policeBoxView);
    }

    @FXML
    public void showUserProfileView() {
        mainContainer.setCenter(userProfileView);
    }
}

package com.roadster.controllers;

import javafx.fxml.FXML;
import javafx.fxml.FXMLLoader;
import javafx.scene.layout.BorderPane;
import javafx.scene.control.ToolBar;
import javafx.scene.Parent;
import com.roadster.views.*;
import java.io.IOException;

/**
 * Main Controller - Handles navigation between different views
 * This is the central controller for the application
 */
public class MainController {

    @FXML
    private BorderPane mainContainer;


    // View instances
    private LoginView loginView;
    private DashboardView dashboardView;
    private DriversView driversView;
    private Parent mapsView; // Changed to Parent for FXML-loaded view
    private MapsController mapsController; // Reference to the maps controller
    private PoliceBoxView policeBoxView;
    private UserProfileView userProfileView;

    @FXML
    public void initialize() {
        // Initialize all views
        loginView = new LoginView(this);
        dashboardView = new DashboardView(this);
        driversView = new DriversView(this);
        policeBoxView = new PoliceBoxView(this);
        userProfileView = new UserProfileView();

        // Initialize FXML-based maps view
        initializeMapsView();

        // Set default view (login)
        showLoginView();
    }

    private void initializeMapsView() {
        try {
            FXMLLoader loader = new FXMLLoader(getClass().getResource("/fxml/MapsView.fxml"));
            mapsView = loader.load();
            mapsController = loader.getController();
            mapsController.setMainController(this);
        } catch (IOException e) {
            System.err.println("Error loading MapsView.fxml: " + e.getMessage());
            e.printStackTrace();
        }
    }


    @FXML
    private void showLoginView() {
        mainContainer.setCenter(loginView);
    }

    @FXML
    public void showDashboardView() {
        mainContainer.setCenter(dashboardView);
    }

    @FXML
    public void showDriversView() {
        mainContainer.setCenter(driversView);
    }

    @FXML
    public void showMapsView() {
        if (mapsView != null) {
            mainContainer.setCenter(mapsView);
        } else {
            System.err.println("MapsView is not properly initialized");
        }
    }

    @FXML
    public void showPoliceBoxView() {
        mainContainer.setCenter(policeBoxView);
    }

    @FXML
    public void showUserProfileView() {
        mainContainer.setCenter(userProfileView);
    }
}

package com.roadster.controllers;

import com.roadster.controllers.MainController;
import javafx.fxml.FXML;
import javafx.fxml.Initializable;
import javafx.scene.control.Button;
import javafx.scene.web.WebView;
import javafx.scene.web.WebEngine;
import javafx.concurrent.Worker;

import java.net.URL;
import java.util.ResourceBundle;

public class MapsController implements Initializable {
    @FXML
    private WebView mapWebView;

    @FXML
    private Button backButton;

    private WebEngine webEngine;
    private MainController mainController;

    public void setMainController(MainController mainController) {
        this.mainController = mainController;
    }

    @Override
    public void initialize(URL url, ResourceBundle resourceBundle) {
        webEngine = mapWebView.getEngine();

        // Load the map HTML file
        URL mapUrl = getClass().getResource("/html/map.html");
        if (mapUrl != null) {
            webEngine.load(mapUrl.toString());
            System.out.println("Loading map from: " + mapUrl.toString());
        } else {
            System.err.println("Could not find map.html file");
        }

        // Enable JavaScript
        webEngine.setJavaScriptEnabled(true);

        // Wait for page to load before enabling interactions
        webEngine.getLoadWorker().stateProperty().addListener((obs, oldState, newState) -> {
            if (newState == Worker.State.SUCCEEDED) {
                System.out.println("Map loaded successfully!");
            } else if (newState == Worker.State.FAILED) {
                System.err.println("Failed to load map");
            }
        });
    }

    @FXML
    protected void onBackToDashboard() {
        if (mainController != null) {
            mainController.showDashboardView();
        }
    }

    // Method to add a marker (for future use when receiving location data)
    public void addMarker(double lat, double lng, String popupText) {
        if (webEngine != null) {
            String script = String.format("window.mapFunctions.addMarker(%f, %f, '%s');", lat, lng, popupText);
            webEngine.executeScript(script);
        }
    }

    // Method to center map on specific coordinates (for future use)
    public void centerMap(double lat, double lng) {
        if (webEngine != null) {
            String script = String.format("window.mapFunctions.centerMap(%f, %f, 15);", lat, lng);
            webEngine.executeScript(script);
        }
    }
}
